SELECT f.nombre AS fabricante, p.nombre AS producto FROM fabricante f JOIN producto p ON f.codigo = p.codigo_fabricante WHERE f.nombre ='Lenovo';
SELECT p.nombre, precio, p.codigo, f.nombre AS fabricante FROM producto p JOIN fabricante f WHERE f.codigo = p.codigo_fabricante AND precio = (SELECT precio FROM producto p, fabricante f WHERE f.codigo = p.codigo_fabricante AND f.nombre='Lenovo' ORDER BY precio DESC LIMIT 0, 1);
