SELECT p.nombre AS producto, precio FROM producto p WHERE precio >= (SELECT precio FROM producto p, fabricante f WHERE f.codigo = p.codigo_fabricante AND f.nombre='Lenovo' ORDER BY precio DESC LIMIT 0, 1);
SELECT p.nombre AS producto, precio FROM producto p, fabricante f WHERE f.codigo = p.codigo_fabricante AND f.nombre='Asus' AND precio > (SELECT AVG(precio) FROM producto p WHERE f.codigo = p.codigo_fabricante);